import React, { Component } from 'react'
import Web3 from 'web3'
import CreateToken from '../abis/CreateToken.json'
// import HDWalletProvider from '@truffle/hdwallet-provider'
// const HDWalletProvider = require('truffle-hdwallet-provider');
// import BuyForm from './BuyForm'
import './App.css'

class Tokencreate extends Component { 

  constructor(props) {
    super(props)
    this.state = {
      name: '',
      symbol: '',
      supply: '',
      decimals: '',
      check: '',
      token: {},
      ethSwap: {},
      ethBalance: '0',
      tokenBalance: '0',
      loading: true,
      button: 1,
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleStop = this.handleSubmit.bind(this);
    this.handleName = this.handleName.bind(this);
    this.handleSymbol = this.handleSymbol.bind(this);
    this.handleSupply = this.handleSupply.bind(this);
    this.handleDecimals = this.handleDecimals.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleName(event) {
    this.setState({name: event.target.value});
  }
  handleSymbol(event) {
    this.setState({symbol: event.target.value});
  }
  handleSupply(event) {
    this.setState({supply: event.target.value});
  }
  handleDecimals(event) {
    this.setState({decimals: event.target.value});
  }

  handleChange(event) {
    this.setState({check: event.target.value});
  }
  handleSubmit(event) {
      if (state.button === 1) {
        alert('Start : Token Name: '+ this.state.name + ' Check ' + this.state.check + ' Token Symbol: '+ this.state.symbol + ' Total Supply: '+ this.state.supply + ' Decimals: '+ this.state.decimals);
      }
      if (state.button === 2) {
        alert('Stop : Token Name: '+ this.state.name + ' Check ' + this.state.check + ' Token Symbol: '+ this.state.symbol + ' Total Supply: '+ this.state.supply + ' Decimals: '+ this.state.decimals);
      }
    alert('Start : Token Name: '+ this.state.name + ' Check ' + this.state.check + ' Token Symbol: '+ this.state.symbol + ' Total Supply: '+ this.state.supply + ' Decimals: '+ this.state.decimals);
    // this.state.token.methods.create(this.state.name,this.state.symbol,this.state.supply,this.state.decimals ).send({ from: this.state.account }).on('transactionHash', (hash) => {
    //   this.setState({ loading: false })
    // })
    event.preventDefault(); 
  }

  handleStop(event) {
    alert('Stop : Token Name: '+ this.state.name + ' Check ' + this.state.check + ' Token Symbol: '+ this.state.symbol + ' Total Supply: '+ this.state.supply + ' Decimals: '+ this.state.decimals);
    // this.state.token.methods.create(this.state.name,this.state.symbol,this.state.supply,this.state.decimals ).send({ from: this.state.account }).on('transactionHash', (hash) => {
    //   this.setState({ loading: false })
    // })
    event.preventDefault(); 
  }

  render() {
    return ( 
      <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '600px' }}>
              <div className="content mr-auto ml-auto">

              <form className="mb-3" onSubmit={this.handleSubmit}>
                <div>
                  <label className="float-left"><b>A</b></label>
                  {/* <span className="float-right text-muted">
                    Balance: {window.web3.utils.fromWei(this.state.ethBalance, 'Ether')}
                  </span> */}
                </div>
                <div className="input-group mb-4">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Input A"
                    value={this.state.name}
                    onChange={this.handleName}
                    required />
                </div>

                <div>
                  <label className="float-left"><b>B</b></label>
                </div>
                <div className="input-group mb-4">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Input B"
                    value={this.state.symbol}
                    onChange={this.handleSymbol}
                    required />
                </div>

                <div>
                  <label className="float-left"><b>C</b></label>
                </div>
                <div className="input-group mb-4">
                  <input
                    type="number"
                    className="form-control form-control-lg"
                    placeholder="Input C"
                    value={this.state.supply}
                    onChange={this.handleSupply}
                    required />
                </div>

                <div>
                  <label className="float-left"><b>D</b></label>
                </div>
                <div className="input-group mb-4">
                  <input
                    type="number"
                    className="form-control form-control-lg"
                    placeholder="Input D"
                    value={this.state.decimals}
                    onChange={this.handleDecimals}
                    required />
                </div>

                <select className="input-group mb-4" value={this.state.check} onChange={this.handleChange}>
                <option value="grapefruit">Grapefruit</option>
                <option value="lime">Lime</option>
                <option value="coconut">Coconut</option>
                <option value="mango">Mango</option>
                  </select>


          <button type="submit" className="btn btn-primary btn-block btn-lg" onClick={() => (state.button = 1)}>Start</button>
          <button  className="btn btn-primary btn-block btn-lg" onClick={() => (state.button = 2)}>Stop</button>
        </form>


              </div>
            </main>
          </div>
        </div>
    ); 
  }
}

export default Tokencreate;
